function App() {

const title  = "Hello Title";

const comments = [
    {id:1, text:"Comment 1"},
    {id:2, text:"Comment 2"},
    {id:3, text:"Comment 3"}
];

const loading = false;
const showComments = false;

if(loading) { return <h1>Loading...</h1> }

    return (
        <>
        <h1>{title}</h1>
        {showComments && (<div className="comments">
        <h2>Comments: ({comments.length})</h2>
         <ul>
            {comments.map((comment, index) => (
                <li key={index}>{comment.text}</li>
            ))}
        </ul>
        </div>) }

        </>  
    )
}


export default App;
