    const handleTextChange = (e) => {
        if(text === '') {
            setBtnDisabled(true);
            setMessage(null);
        } else if(text !== '' && text.trim().length <= 10)

        setText(e.target.value);
    }

    return (
        <Card>
           <form>
               <h2>How would you rate your service with us?</h2>
           {/* @TODO: rating select component */}
